extern crate sequoia_openpgp as openpgp;
use openpgp::serialize::SerializeInto;
use serde::{Serialize, Deserialize};
use super::data;
use super::decrypt_share;

// generates 10 certs (public and private) and outputs them in ascii armor
// see static_approvers_{pub,priv}_10
#[cfg(test)]
// #[test]
#[allow(dead_code)]
fn generate_certs() {
    let uids = vec![
        "alice", "bob", "chester", "david", "eve",
        "fred", "gus", "henrietta", "irene", "jack",
    ];
    let mut pubarmor: Vec<String> = Vec::new();
    let mut privarmor: Vec<String> = Vec::new();
    for name in uids {
        let (cert, _rev) = openpgp::cert::CertBuilder::general_purpose(None, Some(format!("{}@example.org", name))).generate().unwrap();
        let armor = String::from_utf8(cert.armored().to_vec().unwrap()).unwrap();
        pubarmor.push(armor);

        let armor = String::from_utf8(cert.as_tsk().armored().to_vec().unwrap()).unwrap();
        privarmor.push(armor);
    }
    print!("{:#?}\n", pubarmor);
    print!("{:#?}\n", privarmor);
    assert!(false);
}

#[cfg(test)]
// #[test]
#[allow(dead_code)]
// generates 5 shares + signatures and outputs them (encrypted) in ascii armor.
// depends on static_approvers_{pub,priv}_10 and must be regenerated if they
// change. Used to generate test data; see static_shares_3_5().
fn generate_shares() {
    let config = data::KeyConfig {
        kind: data::KeyKind::RSA,
        size: 2048,
        digest: None,
    };
    let approvers = static_approvers_pub_10();
    let generated = super::generate(&approvers[0..5], 3, &config).unwrap();
    let mut shares_strings: Vec<String> = Vec::new();
    for share in generated.shares {
        shares_strings.push(serde_json::to_string(&share).unwrap());
    }
    print!("{:#?}\n", shares_strings);
    assert!(false);
}

#[cfg(test)]
fn static_pubkey() -> data::PubKey {
    let config = data::KeyConfig {
        kind: data::KeyKind::RSA,
        size: 2048,
        digest: None,
    };
    let shares = &static_shares_3_5();
    let privkey_pem = super::recover(3, &shares[0..3]).unwrap();
    super::tls::public_from_private(&config, &privkey_pem).unwrap()
}

#[cfg(test)]
#[derive(Serialize, Deserialize)]
pub struct TestData {
    pub config: data::KeyConfig,
    pub pubkey: data::PubKey,
    pub shares: Vec<data::EncryptedShare>,
    pub shares_required: u8,
    pub approvers_pub: Vec<String>,
    pub approvers_priv: Vec<String>,
}

impl TestData {
    pub fn decrypted_shares(&self) -> Vec<data::Share> {
        self.shares.clone().into_iter().zip(self.approvers_priv.iter())
            .map(|(share, key)| decrypt_share(key.as_bytes(), share).unwrap())
            .collect()
    }
}

#[allow(dead_code)]
pub fn generate_tsks(total: u8) -> Vec<openpgp::Cert> {
    let uids = vec![
        "alice", "bob", "chester", "david", "eve",
        "fred", "gus", "henrietta", "irene", "jack",
    ];
    let mut x = 0;
    std::iter::repeat_with(
        || { let tmp = x; x = (x + 1) % uids.len(); &uids[tmp] }
    ).take(total.into()).map(|name| {
        let (cert, _rev) = openpgp::cert::CertBuilder::general_purpose(None, Some(format!("{}@example.org", name))).generate().unwrap();
        cert
    }).collect()
}

#[allow(dead_code)]
pub fn generate_test_data(total: u8, required: u8) -> TestData {
    let config = data::KeyConfig {
        kind: data::KeyKind::RSA,
        size: 2048,
        digest: None,
    };
    let approvers = generate_tsks(total);
    let approvers_pub: Vec<String> = approvers.iter().map(
        |x| String::from_utf8(x.armored().to_vec().unwrap()).unwrap()
    ).collect();
    let approvers_priv: Vec<String> = approvers.iter().map(
        |x| String::from_utf8(x.as_tsk().armored().to_vec().unwrap()).unwrap()
    ).collect();

    let generated = super::generate(approvers_pub.as_slice(), required, &config).unwrap();
    TestData {
        config,
        pubkey: generated.pubkey,
        shares: generated.shares,
        shares_required: required,
        approvers_pub,
        approvers_priv,
    }
}

#[cfg(test)]
//#[test]
#[allow(dead_code)]
pub fn generate_3_5() {
    let td = generate_test_data(5, 3);
    print!("{}\n", serde_json::to_string(&td).unwrap());
    assert!(td.approvers_pub.len() == 5);
    assert!(false);
}

pub fn test_data_3_5() -> TestData {
    let config = data::KeyConfig {
        kind: data::KeyKind::RSA,
        size: 2048,
        digest: None,
    };
    let pubkey = static_pubkey();
    let shares = Vec::from(static_shares_3_5_encrypted());
    let shares_required = 5;
    let approvers_pub = Vec::from(static_approvers_pub_10());
    let approvers_priv = Vec::from(static_approvers_priv_10());
    TestData {
        config,
        pubkey,
        shares,
        shares_required,
        approvers_pub,
        approvers_priv,
    }
}

